package com.beckettit.hunter.dm.task

import com.beckettit.Env
import com.beckettit.transform.*
import com.beckettit.process.Util
import com.beckettit.salesforce.*
import com.beckettit.salesforce.soap.*
import com.beckettit.jdbc.*
import com.beckettit.hunter.dm.obj.*
import org.apache.log4j.Logger

class LoadCases extends BaseTask {
    Boolean insert = false
    Boolean update = false

    public LoadCases(String[] args){
        super(args)
        insert = args?.findAll {
            it == '--insert'
        }.size() > 0

        update = args?.findAll {
            it == '--update'
        }.size() > 0
    }

    public void run() {
        if(insert){
            ObjectDef objDef = Case.getInsertObjectDef()
            String query = """
            select * from (
                select ${objDef.getMappedColumnsForQuery('mig')} from ${objDef.table} mig
                left outer join ref_case ref on ref.external_id = mig.External_ID__c 
                where ref.id is null and mig.OwnerId is not null
            ) m
            """
            println query
            soap.upsertAll("insert-cases-owner", objDef, query, [batchSize:50])
            
            query = """
            select * from (
                select ${objDef.getMappedColumnsForQuery('mig')} from ${objDef.table} mig
                left outer join ref_case ref on ref.external_id = mig.External_ID__c 
                where ref.id is null and mig.OwnerId is null
            ) m
            """
            println query
            objDef.mapping.remove("OwnerId")
            soap.upsertAll("insert-cases-noowner", objDef, query, [batchSize:50])
            Util.backupSaveResults(jdbc, "cases")
        }

        if(update){
            ObjectDef objDef = Case.getUpdateObjectDef()
            String query = """
            select * from (
                select ${objDef.getMappedColumnsForQuery('mig')} from ${objDef.table} mig
                inner join ref_case ref on ref.external_id = mig.External_ID__c 
                where mig.OwnerId is not null
            ) m
            """
            println query
            soap.upsertAll("update-cases-owner", objDef, query, [batchSize:50])
            
            query = """
            select * from (
                select ${objDef.getMappedColumnsForQuery('mig')} from ${objDef.table} mig
                inner join ref_case ref on ref.external_id = mig.External_ID__c 
                where mig.OwnerId is null
            ) m
            """
            println query
            objDef.mapping.remove("OwnerId")
            soap.upsertAll("update-cases-noowner", objDef, query, [batchSize:50])
            Util.backupSaveResults(jdbc, "cases_u")
        }

    }

    //main task
    static void main(String[] args) {
        LoadCases proc = new LoadCases(args)
        proc.run()
    }
}
